import * as React from "react";
import { SearchQuery } from "../../SearchBox";
import css from "./WhereInput.module.scss";

type Props = {
  setSearchQuery: React.Dispatch<React.SetStateAction<SearchQuery>>;
  placeType: string;
};

export const WhereInput: React.FC<Props> = ({ setSearchQuery, placeType }) => {
  const handleChange: (event: React.ChangeEvent<HTMLInputElement>) => void = (
    event
  ) => {
    return setSearchQuery((state) => ({
      ...state,
      placeType: event.target.value,
    }));
  };

  return (
    <div className={css.where}>
      <label htmlFor="">Where are you meeting?</label>
      <input
        type="text"
        placeholder="Los Angeles / 90015"
        value={placeType}
        onChange={(event) => handleChange(event)}
      />
      <select name="radius">
        <optgroup>
          <option value="">Radius...</option>
          <option value="">Within 1 mile</option>
          <option value="">Within 5 miles</option>
          <option value="">Within 10 miles</option>
          <option value="">Within 25 miles</option>
        </optgroup>
      </select>
    </div>
  );
};
