export const yelpEventsCall = async ({
  location,
  radius,
  unixStartDate,
  unixEndDate,
  categories
}) => {
  try {
    const response = await fetch("/api/yelpEventsAPI", {
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      method: "POST",
      body: JSON.stringify({
        location,
        radius: parseInt(radius),
        start_date: parseInt(unixStartDate),
        end_date: parseInt(unixEndDate)
      })
    });
    const responseJson = await response.json();
    const {
      events
    } = responseJson;
    events.forEach(event => (event["type"] = "event", event["source"] = "yelp", event["inParts"] = false));
    return events;
  } catch (err) {
    return err.message;
  }
};
