import * as React from "react";
import type { InputChange } from "../../SearchBox";
import css from "./WhereInput.module.scss";

type Props = {
  handleInputChange: InputChange;
  placeType: string;
  radius: string;
};

export const WhereInput: React.FC<Props> = ({
  placeType,
  handleInputChange,
  radius,
}) => {
  const handleChange: (
    event: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>
  ) => void = () => {
    return handleInputChange(
      "placeType",
      (event.target as HTMLInputElement | HTMLSelectElement).value
    );
  };
  return (
    <div className={css.where}>
      <label htmlFor="">Where are you meeting?</label>
      <input
        type="text"
        placeholder="Los Angeles / 90015"
        value={placeType}
        onChange={handleChange}
      />
      <select name="radius" value={radius} onChange={handleChange}>
        <optgroup>
          <option value="">Radius...</option>
          <option value="">Within 1 mile</option>
          <option value="">Within 5 miles</option>
          <option value="">Within 10 miles</option>
          <option value="">Within 25 miles</option>
        </optgroup>
      </select>
    </div>
  );
};
