export async function getServerSideProps({
  query,
  params
}) {
  if (query.queried) {
    const checkURLIsString = query.queried.toString();
    const paramValueArray = checkURLIsString.split("+");
    const searchParamsValues = {};
    paramValueArray.forEach(param => {
      const indexOfEqual = param.search("=");
      if (indexOfEqual === -1) return;else {
        const paramKey = param.substring(0, indexOfEqual);
        const paramValue = param.substring(indexOfEqual + 1);
        searchParamsValues[paramKey] = paramValue;
      }
    });
    const {
      location,
      radius,
      placeType
    } = searchParamsValues;
    const yelpBusinesses = new URL("https://api.yelp.com/v3/businesses/search"),
          params = {
      location,
      radius,
      term: placeType
    };
    Object.keys(params).forEach(key => yelpBusinesses.searchParams.append(key, params[key]));
    const yelpUrlString = yelpBusinesses.toString(); //function

    const yelpResponse = await fetch(yelpUrlString, {
      headers: {
        Authorization: `Bearer ${process.env.YELP_API_KEY}`
      }
    });
    const responseJson = await yelpResponse.json();
    const {
      businesses
    } = responseJson;
    businesses.forEach(business => (business["type"] = "place", business["source"] = "yelp", business["inParts"] = false));
    return {
      props: {
        results: businesses
      }
    };
  } else {
    return {
      props: {
        results: {},
        searchType: "ALL"
      }
    };
  }
}
