import * as React from "react";
import DatePicker from "react-datepicker";
import InputProps from "../../SearchBox";
import css from "./WhenInput.module.scss";

type Props = Pick<InputProps, "startDate" | "handleInputChange" | "endDate">;

export const WhenInput: React.FC<Props> = ({
  handleInputChange,
  startDate,
  endDate,
}) => {
  const handleDateChange: (field: string, date: Date) => void = (
    field,
    date
  ) => {
    return handleInputChange(field, date);
  };
  return (
    <div className={css.when}>
      <label htmlFor="">When are you meeting?</label>
      <DatePicker
        className={css.from}
        placeholderText={"Start time"}
        autoComplete="off"
        selected={startDate}
        minDate={new Date()}
        dateFormat="Pp"
        showTimeSelect
        onChange={(event) => handleChange(event, "startDate")}
      ></DatePicker>
      <DatePicker
        className={css.to}
        placeholderText={"End Time"}
        autoComplete="off"
        minDate={new Date()}
        selected={endDate}
        dateFormat="Pp"
        showTimeSelect
        onChange={(event) => handleChange(event, "endDate")}
      ></DatePicker>
    </div>
  );
};
