import * as React from "react";
import DatePicker from "react-datepicker";
import InputProps from "../../SearchBox";
import css from "./WhenInput.module.scss";
import { start } from "repl";

type Props = Pick<InputProps, "startDate" | "handleInputChange" | "endDate">;

export const WhenInput: React.FC<Props> = ({
  handleInputChange,
  startDate,
  endDate,
}) => {
  const handleDateChange: (field: string, date: Date) => void = (
    field,
    date
  ) => {
    return handleInputChange(field, date);
  };

  const today = new Date();

  return (
    <div className={css.when}>
      <label htmlFor="">When are you meeting?</label>
      <DatePicker
        className={css.from}
        placeholderText={"Start time"}
        autoComplete="off"
        selected={startDate}
        minTime={today.setHours(today.getHours())}
        maxTime={today.setHours(24)}
        minDate={today}
        dateFormat="Pp"
        showTimeSelect
        onChange={(date) => handleDateChange("startDate", date)}
      ></DatePicker>
      <DatePicker
        className={css.to}
        placeholderText={"End Time"}
        autoComplete="off"
        minDate={startDate ? startDate : today}
        maxTime={24}
        selected={endDate}
        dateFormat="Pp"
        showTimeSelect
        onChange={(date) => handleDateChange("endDate", date)}
      ></DatePicker>
    </div>
  );
};
