import * as React from "react";
import {
  SearchSelector,
  WhereInput,
  WhenInput,
  EventsInput,
  PlacesInput,
} from "./Parts";
import Link from "next/link";
import css from "./SearchBox.module.scss";

export type InputChange = (key: string, input: string | Date) => void;

export default interface InputProps {
  handleInputChange: InputChange;
  placeType: string;
  radius: string;
  eventsCategory: string;
  where: string;
  startDate: Date;
  endDate: Date;
  handleQueryDate: (key: string, date: Date) => void;
}

interface SearchQuery {
  searchType: string;
  eventsCategory: string;
  radius: string;
  where: string;
  startTime: number;
  endTime: number;
  placeType: string;
  endDate: Date;
  startDate: Date;
  startFormatted: string;
  endFormatted: string;
  unixStartDate: number;
  unixEndDate: number;
  ticketMasterCategories: string;
  yelpCategories: string;
}

export const SearchBox: React.FC = () => {
  const [searchQuery, setSearchQuery] = React.useState<SearchQuery>({
    searchType: "ALL",
    eventsCategory: "",
    radius: "",
    startTime: 0,
    where: "",
    endTime: 0,
    endDate: null,
    startDate: null,
    placeType: "",
    startFormatted: "",
    endFormatted: "",
    unixStartDate: null,
    unixEndDate: null,
    ticketMasterCategories: "",
    yelpCategories: "",
  });

  const setDynamicPage: () => string = () => {
    return "";
  };

  const linked = () => {
    return (
      <Link
        href="/search/[query]"
        as={`/search/${searchQuery.searchType}+${setDynamicPage()}`}
      >
        <button>Search</button>
      </Link>
    );
  };

  const handleInputChange: InputChange = (key, input) => {
    return setSearchQuery((state) => ({
      ...state,
      [key]: input,
    }));
  };

  const handleQueryDate: (key: string, date: Date) => void = (key, date) => {
    if (key === "startDate") {
      const unixStartDate: number = Math.round(new Date(date).getTime() / 1000);
      console.log(date.getTimezoneOffset());
      console.log(date.toISOString());
      console.log(unixStartDate);

      return setSearchQuery((state) => ({
        ...state,
        [key]: date,
        unixStartDate,
      }));
    } else {
      const unixEndDate: number = Math.round(new Date(date).getTime() / 1000);
      console.log(unixEndDate);

      return setSearchQuery((state) => ({
        ...state,
        [key]: date,
        unixEndDate,
      }));
    }
  };

  console.log(searchQuery);

  return (
    <div className={css.searchBox}>
      <SearchSelector></SearchSelector>
      <WhereInput
        handleInputChange={handleInputChange}
        where={searchQuery.where}
        radius={searchQuery.radius}
      ></WhereInput>
      <WhenInput
        handleQueryDate={handleQueryDate}
        startDate={searchQuery.startDate}
        endDate={searchQuery.endDate}
      ></WhenInput>
      <EventsInput
        handleInputChange={handleInputChange}
        eventsCategory={searchQuery.eventsCategory}
      ></EventsInput>
      <PlacesInput
        handleInputChange={handleInputChange}
        placeType={searchQuery.placeType}
      ></PlacesInput>
      <button className={css.searchButton}>Search</button>
    </div>
  );
};
