export const ticketMasterCall = async ({
  location,
  radius,
  startFormatted,
  endFormatted
}) => {
  try {
    const response = await fetch("http://localhost:3000/api/ticketmasterAPI", {
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      method: "POST",
      body: JSON.stringify({
        location,
        radius: Math.floor(parseInt(radius) * 0.001).toString(),
        startDateTime: startFormatted,
        endDateTime: endFormatted
      })
    });
    const responseJson = await response.json();
    const {
      _embedded: {
        events
      }
    } = responseJson;
    events.forEach(event => (event["source"] = "ticketmaster", event["type"] = "event", event["inParts"] = false));
    return events;
  } catch (err) {
    return err.message;
  }
};
