  //   }
  // };

  const determineImageRatio: () => string = () => {
    return source === "yelp" ? css.byWidth : css.byWidth;
  };

  return (
    <div
      className={`${css.imgBackground} ${extended ? css.extended : null}`}
      onClick={handleRetract}
    >
      {children}
    </div>
  );
};
