export async function getServerSideProps({
  query,
  params,
}): Promise<{ props: { results: any; searchType?: string } }> {
  if (query.queried) {
    const checkURLIsString: string = query.queried.toString();
    const paramValueArray: string[] = checkURLIsString.split("+");

    const searchParamsValues: { [key: string]: string } = {};

    paramValueArray.forEach((param) => {
      const indexOfEqual: number = param.search("=");
      if (indexOfEqual === -1) return;
      else {
        const paramKey: string = param.substring(0, indexOfEqual);
        const paramValue: string = param.substring(indexOfEqual + 1);
        searchParamsValues[paramKey] = paramValue;
      }
    });

    const { location, radius, placeType } = searchParamsValues;
    const yelpBusinesses: URL = new URL(
        "https://api.yelp.com/v3/businesses/search"
      ),
      params: Params = {
        location,
        radius,
        term: placeType,
      };
    Object.keys(params).forEach((key) =>
      yelpBusinesses.searchParams.append(key, params[key])
    );

    const yelpUrlString: string = yelpBusinesses.toString();

    //function

    const yelpResponse: Response = await fetch(yelpUrlString, {
      headers: {
        Authorization: `Bearer ${process.env.YELP_API_KEY}`,
      },
    });

    const responseJson = await yelpResponse.json();

    const {
      businesses,
    }: { businesses: { [key: string]: any }[] } = responseJson;
    businesses.forEach(
      (business) => (
        (business["type"] = "place"),
        (business["source"] = "yelp"),
        (business["inParts"] = false)
      )
    );

    return {
      props: {
        results: businesses,
      },
    };
  } else {
    return { props: { results: {}, searchType: "ALL" } };
  }
}

export default Queried;

